import React, { useState, useEffect, useRef } from 'react';

const ChatBot = () => {
  const [messages, setMessages] = useState([
    { from: 'bot', text: 'Hello! I am your assistant. Ask me anything about Kenneth or his portfolio.', avatar: 'ðŸ¤–' }
  ]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const getBotResponse = (userInput) => {
    const input = userInput.toLowerCase().trim();

    // Check for gibberish: too many repeated characters, undefined, repeated words, or very short
    const gibberishRegex = /(.)\1{2,}/g; // 3 or more repeated characters
    const repeatedCount = (input.match(gibberishRegex) || []).length;
    const specialChars = (input.match(/[^a-z\s]/g) || []).length;
    const totalLength = input.length;
    const words = input.split(/\s+/);
    const uniqueWords = new Set(words);
    const repeatedWords = words.length - uniqueWords.size > 1; // More than 1 repeated word

    if (repeatedCount > 2 || specialChars > totalLength * 0.3 || input.includes('undefined') || totalLength < 3 || repeatedWords) {
      return 'Whoa, that looks like some cosmic code! ðŸŒŒ Can you try asking in plain English? I\'m here to help with Kenneth\'s portfolio! ðŸš€';
    }

    // Simple grammar check: check for common errors
    const grammarIssues = [];
    if (input.includes('i ') && !input.includes(' i ')) grammarIssues.push('Capitalize "I"');
    if (input.split('.').length > 1 && !input.includes('?') && !input.includes('!')) grammarIssues.push('End sentences with punctuation');
    if (grammarIssues.length > 0) {
      return `Nice try! Just a quick tip: ${grammarIssues.join(', ')}. Now, about Kenneth's portfolio... ðŸš€`;
    }

    const responses = {
      greeting: ['Hey there! Ready to explore Kenneth\'s awesome portfolio? ðŸŒŸ', 'Hi! I\'m excited to tell you about Kenneth! ðŸ˜Š', 'Hello! Let\'s dive into some cool tech stuff! ðŸ’»'],
      portfolio: ['Kenneth is a superstar Junior Engineer! Check out his skills and projects - they\'re mind-blowing! âœ¨', 'Kenneth rocks at full-stack development. His portfolio is a tech adventure! ðŸ†'],
      skills: ['Kenneth masters Frontend (React magic!), Backend (Flask wizardry), and DevOps (cloud conqueror)! âš¡', 'His skills? Epic! React, Python, Docker, AWS - he\'s got it all! ðŸ”¥'],
      projects: ['Kenneth built amazing projects like Moringa Daily Dev and Music App. Pure genius! ðŸŽµ', 'His projects are game-changers! From e-commerce to social apps, he\'s unstoppable! ðŸš€'],
      contact: ['Reach Kenneth at kennjorothuo@gmail.com. He\'d love to hear from you! ðŸ“§', 'Contact him for epic collaborations! kennjorothuo@gmail.com ðŸ’Œ'],
      bye: ['See you later! Keep coding awesome! ðŸ‘‹', 'Bye! Don\'t forget to check out Kenneth\'s work! ðŸŒˆ'],
      default: ['That\'s fascinating! Want to know more about Kenneth\'s skills or projects? ðŸ¤”', 'Cool! Ask me about his experience or tech stack! ðŸ’¡', 'Awesome question! I\'m all about Kenneth\'s portfolio vibes! ðŸŽ‰']
    };

    if (input.includes('hello') || input.includes('hi')) {
      return responses.greeting[Math.floor(Math.random() * responses.greeting.length)];
    } else if (input.includes('kenneth') || input.includes('portfolio')) {
      return responses.portfolio[Math.floor(Math.random() * responses.portfolio.length)];
    } else if (input.includes('skills') || input.includes('expertise')) {
      return responses.skills[Math.floor(Math.random() * responses.skills.length)];
    } else if (input.includes('projects')) {
      return responses.projects[Math.floor(Math.random() * responses.projects.length)];
    } else if (input.includes('contact') || input.includes('email')) {
      return responses.contact[Math.floor(Math.random() * responses.contact.length)];
    } else if (input.includes('bye') || input.includes('goodbye')) {
      return responses.bye[Math.floor(Math.random() * responses.bye.length)];
    } else {
      return responses.default[Math.floor(Math.random() * responses.default.length)];
    }
  };

  const typeMessage = (text) => {
    setIsTyping(true);
    const newMessage = { from: 'bot', text: text, avatar: 'ðŸ¤–' };
    setMessages((prev) => [...prev, newMessage]);
    setIsTyping(false);
  };

  const handleSend = async () => {
    if (!input.trim() || isTyping) return;

    const userMessage = { from: 'user', text: input, avatar: 'ðŸ‘¤' };
    setMessages((prev) => [...prev, userMessage]);
    const userInput = input;
    setInput('');

    // Get bot response
    const response = getBotResponse(userInput);

    // Simulate typing delay
    setTimeout(() => {
      typeMessage(response);
    }, 500);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <section className="py-20 bg-cover bg-center" style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1506744038136-46273834b3fb?ixlib=rb-4.0.3&auto=format&fit=crop&w=1470&q=80)' }}>
      <div className="container mx-auto px-6 max-w-3xl bg-white bg-opacity-90 rounded-lg shadow-lg p-6">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Chat with My AI Assistant</h2>
        <div className="h-64 overflow-y-auto border border-gray-300 rounded p-4 mb-4 bg-gray-50">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`mb-4 flex items-end ${msg.from === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              {msg.from === 'bot' && <div className="text-2xl mr-2">{msg.avatar}</div>}
              <div className={`rounded-lg px-4 py-2 max-w-xs shadow-md ${msg.from === 'user' ? 'bg-blue-600 text-white' : 'bg-white text-gray-800 border'}`}>
                {msg.text}
              </div>
              {msg.from === 'user' && <div className="text-2xl ml-2">{msg.avatar}</div>}
            </div>
          ))}
          {isTyping && (
            <div className="mb-4 flex items-end justify-start">
              <div className="text-2xl mr-2">ðŸ¤–</div>
              <div className="rounded-lg px-4 py-2 bg-white text-gray-800 border shadow-md">
                <div className="flex space-x-1">
                  <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce"></div>
                  <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                  <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        <textarea
          className="w-full border border-gray-300 rounded p-2 mb-2 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="3"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type your message here..."
          disabled={isTyping}
        />
        <button
          onClick={handleSend}
          disabled={isTyping}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 rounded transition duration-300 disabled:opacity-50"
        >
          {isTyping ? 'AI is thinking...' : 'Send'}
        </button>
      </div>
    </section>
  );
};

export default ChatBot;
