import React, { useState, useEffect, useRef } from 'react';

const ChatBot = () => {
  const [messages, setMessages] = useState([
    { from: 'bot', text: 'Hello! I am your assistant. Ask me anything.' }
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { from: 'user', text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');

    // Simulate bot response with a delay
    setTimeout(() => {
      // For demo, echo user input reversed as bot response
      const botResponse = {
        from: 'bot',
        text: `You said: "${input}". (This is a demo response.)`
      };
      setMessages((prev) => [...prev, botResponse]);
    }, 1000);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <section className="py-20 bg-cover bg-center" style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1506744038136-46273834b3fb?ixlib=rb-4.0.3&auto=format&fit=crop&w=1470&q=80)' }}>
      <div className="container mx-auto px-6 max-w-3xl bg-white bg-opacity-90 rounded-lg shadow-lg p-6">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Chat with Me</h2>
        <div className="h-64 overflow-y-auto border border-gray-300 rounded p-4 mb-4 bg-gray-50">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`mb-2 flex ${msg.from === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div className={`rounded-lg px-4 py-2 max-w-xs ${msg.from === 'user' ? 'bg-blue-600 text-white' : 'bg-gray-300 text-gray-800'}`}>
                {msg.text}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <textarea
          className="w-full border border-gray-300 rounded p-2 mb-2 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="3"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type your message here..."
        />
        <button
          onClick={handleSend}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 rounded transition duration-300"
        >
          Send
        </button>
      </div>
    </section>
  );
};

export default ChatBot;
